const StyledFooter = styled.footer`
    position:relative;
    overflow:hidden;
    padding-top: 60px;
    height:98vh;
    font-family: Verdana, Geneva, Tahoma, sans-serif;
    font-size:0.875rem;
    background: rgb(233, 233, 233);
    backdrop-filter: blur(15px);
    color: #29253D;
    .bg {
        position:absolute;
        z-index:-10;
        left:0;
        bottom:0;
        svg {
            stroke: rgb(0,26,148);
            opacity:0.1;
        }
    }
    .footer-top {
        .icons-ctn {
        display:flex;
        align-items: flex-start;
        gap:10px;
        }
        .industry-item {
            padding-block:5px;
            text-transform: capitalize;
            .industry-item-inner {
            text-decoration:none;
            }
            .industry-item-inner:visited {
            color:inherit;
            }
        }
        .contact {
            padding-block:5px;
        }
        .footer-menu {
            
            .menu-item {
                padding-block:5px;
                .menu-item-inner {
                    text-decoration:none;
                }
                .menu-item-inner:visited {
                    color:inherit;
                }
            }
        }
    }
    .footer-bottom {
        display:grid;
        padding-block: 20px;
    }
    @media screen and (min-width:768px) {
        .footer-bottom {
            display:flex;
            justify-content: space-between;
        }
    }
`